from PIL import Image  # pip install pillow
import os, base64, io, ctypes


# 开启Windows的虚拟终端序列支持
def enableVTMode():
  kernel32 = ctypes.windll.kernel32
  hOut = kernel32.GetStdHandle(-11)
  if hOut == -1:
    print("获取窗口句柄失败！")
    exit(0)

  dwMode = ctypes.c_ulong(0)
  if not kernel32.GetConsoleMode(hOut, ctypes.byref(dwMode)):
      print("获取ConsoleMode失败！")
      exit(0)

  dwMode.value = dwMode.value | 0x0004
  if not kernel32.SetConsoleMode(hOut, dwMode):
      print("开启虚拟终端失败！")
      exit(0)


def colorAnsi(R:int, G:int, B:int) -> str:
    return f"\033[38;2;{R};{G};{B}m"


